#include <bits/stdc++.h>using namespace std ;int main (){stack<char>st ;int n ; cin >> n;    while(n--){        stack<char> st;        int x;        cin >> x;        int ans = 0;        for(int i = 0 ; i < x ; i++ ){            char c;            cin >> c;            if(c == '('){                st.push(c);            }             else {                if(!st.empty()){                    st.pop();                } else {                    ans++;                }            }        }int y = st.size() ;        int result = min(y , ans);        cout << result << endl;    }    return 0;}