#include <bits/stdc++.h>using namespace std;int main(){	vector<long long> pr;	for (long long i = 2; i <= 100000; i++) 	{		bool is_p = true;		for (long long j = 2; j * j <= i; j++) 		{			if (i % j == 0)			{				is_p = false;				break;			}		}		if (is_p)			pr.push_back(i);	}	int t;	cin >> t;	while (t--) 	{		long long d;		cin >> d;		long long p = -1;		for (long long i = 0; i < pr.size(); i++) 		{			if (pr[i] >= (1 + d))			{				p = pr[i];				break;			}		}		long long q = -1;		for (long long i = 0; i < pr.size(); i++)		{			if (pr[i] >= (p + d))			{				q = pr[i];				break;			}		}		long long a = min(1LL * p * p * p, 1LL * p * q);		cout << a << endl;	}}