#include <bits/stdc++.h>using namespace std;int main(){    int t;    cin>>t;    while(t--){        string a,b;        cin>>a>>b;        int mx=0;        for(int i=0;i<a.size();i++){            for(int len=1;i+len<=a.size();len++){                string sub=a.substr(i,len);                if(b.find(sub)!=string::npos){                    mx=max(mx,len);                }            }        }        cout<<a.size()+b.size()-2*mx<<"\n";    }}