#include <bits/stdc++.h>using namespace std; int main() {	int t;	cin>>t;	while(t--) {	    int n;	    cin>>n;	    int power=0;	    if(n<=2){	        for(int i=0; i<n; i++){	            cout<<i<<" ";	        }	        cout<<endl;	        continue;	    }	    if(n==3){	        cout<<1<<" "<<0<<" "<<2<<endl;	        continue;	    }	    for(int i=0; i<n; i++){	        if(pow(2,i)<n){	            power=pow(2,i);	        }	        else{	            break;	        }	    }	    for(int i=power-1; i>=0; i--){	        cout<<i<<" ";	    }	    for(int i=power; i<n; i++){	        cout<<i<<" ";	    }	    cout<<endl;	} }