//greedy#pragma GCC optimize("O3,unroll-loops") #include <bits/stdc++.h>#include <ext/pb_ds/assoc_container.hpp>#include <ext/pb_ds/tree_policy.hpp> using namespace std;using namespace __gnu_pbds; using ll = long long;using pii = pair<int, int>;using pll = pair<ll, ll>;using vi = vector<int>;using vll = vector<ll>; const int INF = 1e9;const ll LINF = 1e18;const int MOD = 1e9 + 7; // or 998244353 #define all(x) (x).begin(), (x).end()#define rall(x) (x).rbegin(), (x).rend()#define sz(x) (int)(x).size()#define pb push_back#define fi first#define se second#define mp make_pair template <typename T>using ordered_set = tree<T, null_type, less<T>, rb_tree_tag, tree_order_statistics_node_update>; struct custom_hash {    static uint64_t splitmix64(uint64_t x) {        x += 0x9e3779b97f4a7c15;        x = (x ^ (x >> 30)) * 0xbf58476d1ce4e5b9;        x = (x ^ (x >> 27)) * 0x94d049bb133111eb;        return x ^ (x >> 31);    }     size_t operator()(uint64_t x) const {        static const uint64_t FIXED_RANDOM = chrono::steady_clock::now().time_since_epoch().count();        return splitmix64(x + FIXED_RANDOM);    }}; void solve() {    int n;    ll x;    cin >> n >> x;    ll curr = 0;    ll maxi = -4e18;     for(int i = 0; i < n; i++) {        ll a, b, c;        cin >> a >> b >> c;        curr += (b - 1) * a;        ll cycle_val = a * b - c;        maxi = max(maxi, cycle_val);    }    if(curr >= x) {        cout << 0 << "\n";        return;    }    if(maxi <= 0) {        cout << -1 << "\n";        return;    }    ll needed = x - curr;    ll ans = (needed + maxi - 1) / maxi;    cout << ans << "\n";} int main() {    // Fast I/O    ios_base::sync_with_stdio(false);    cin.tie(NULL);     int t = 1;    cin >> t;      while (t--) {        solve();    }     return 0;}