#include<bits/stdc++.h>using namespace std;using ll=long long;int n;ll a[200005],b[200005];int main(){	ios::sync_with_stdio(false);cin.tie(0);	int T; cin>>T;	while(T--){		cin>>n;		for(int i=1;i<=n;i++) cin>>a[i];		for(int i=1;i<=n;i++) cin>>b[i];		sort(a+1,a+n+1,greater());		ll mx=0,tot=0;		for (int i=1;i<=n;i++) {			tot+=b[i];			if(tot>n) break;			mx=max(mx,a[tot]*i);		}		cout<<mx<<"\n";	}	return 0;}