#pragma GCC optimize("O3,unroll-loops") #include <bits/stdc++.h>#include <ext/pb_ds/assoc_container.hpp>#include <ext/pb_ds/tree_policy.hpp> using namespace std;using namespace __gnu_pbds; using ll = long long;using pii = pair<int, int>;using pll = pair<ll, ll>;using vi = vector<int>;using vll = vector<ll>; const int INF = 1e9;const ll LINF = 1e18;const int MOD = 1e9 + 7; // or 998244353 #define all(x) (x).begin(), (x).end()#define rall(x) (x).rbegin(), (x).rend()#define sz(x) (int)(x).size()#define pb push_back#define fi first#define se second#define mp make_pair template <typename T>using ordered_set = tree<T, null_type, less<T>, rb_tree_tag, tree_order_statistics_node_update>; struct custom_hash {    static uint64_t splitmix64(uint64_t x) {        x += 0x9e3779b97f4a7c15;        x = (x ^ (x >> 30)) * 0xbf58476d1ce4e5b9;        x = (x ^ (x >> 27)) * 0x94d049bb133111eb;        return x ^ (x >> 31);    }     size_t operator()(uint64_t x) const {        static const uint64_t FIXED_RANDOM = chrono::steady_clock::now().time_since_epoch().count();        return splitmix64(x + FIXED_RANDOM);    }}; void solve() {    int n, m;    cin >> n >> m;    vector<vi> a(n, vi(m));    int tot = 0;    for (int i = 0; i < n; i++) {        for (int j = 0; j < m; j++) {            cin >> a[i][j];            tot += a[i][j];        }    }    vector<vi> p(n, vi(m + 1, 0));    for (int i = 0; i < n; i++)        for (int j = 0; j < m; j++)             p[i][j + 1] = p[i][j] + a[i][j];    vector<vi> mn(n + 1, vi(m + 1, 1e9)), mx(n + 1, vi(m + 1, -1e9));    mn[0][0] = mx[0][0] = 0;    for (int i = 0; i <= n; i++) {        for (int j = 0; j <= m; j++) {            if (i > 0) {                int val = p[i - 1][j];                mn[i][j] = min(mn[i][j], mn[i - 1][j] + val);                mx[i][j] = max(mx[i][j], mx[i - 1][j] + val);            }            if (j > 0) {                mn[i][j] = min(mn[i][j], mn[i][j - 1]);                mx[i][j] = max(mx[i][j], mx[i][j - 1]);            }        }    }    int k = tot / 2;    if (k < mn[n][m]) k = mn[n][m];    if (k > mx[n][m]) k = mx[n][m];    cout << 1LL * k * (tot - k) << "\n";    string ans = "";    int i = n, j = m, cur = k;    while (i > 0 || j > 0) {        if (i > 0) {            int req = cur - p[i - 1][j];            if (req >= mn[i - 1][j] && req <= mx[i - 1][j]) {                ans += 'D';                i--;                cur = req;                continue;            }        }        ans += 'R';        j--;    }    reverse(ans.begin(), ans.end());    cout << ans << "\n";} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t = 1;    cin >> t;     while (t--)         solve();    return 0;}